}

const SideBar = ({ modules }) => (
<aside>
  { modules.map(module =>(
    <div key={module.id}>
      <strong>{module.title}</strong>
        <ul>
             { module.lessons.map(lesson =>(
               <li key={lesson.id}>{lesson.title}
               <button onClick={() => {} } />
               </li>  
             ))}
        </ul>
    </div> 
    )) }
</aside>
);

export default connect(state => ({ modules: state.modules }))(SideBar);
